static const int materialNumber = 100;

static inline bool  lookupCTDMaterial(std::string dataName, CTDMaterial& material) {
	for (int i = 0; i < materialNumber; i++)
		if (ctdData[i].name == dataName) {
			material = ctdData[i];
			return true;
		}
	return false; 
}

