static const int materialNumber = 100;

static SmoothMaterial lookupSmoothMaterial(std::string dataName) {
	for (int i = 0; i < materialNumber; i++)
		if (smoothData[i].name == dataName)
			return smoothData[i];

	SLog(EError, "Material \"%s\" not found, returning %s.", dataName.c_str(),
			smoothData[0].name.c_str());
	return smoothData[0];
}

